import Vuex from 'vuex';

import data from '../api/data'
export default new Vuex.Store({
    state:{
        students:[],
        csk:[],
        rcb:[]
    },
    getters:{},
    actions:{
        getStudents(context){
            context.commit('setStudents')
        },
        addTeamMember(context,data){
            context.commit('pushMemberToState')
        }
    },
    mutations:{
        setStudents(state){
            state.students = data.getStudents()
        },
        pushMemberToState(state, payload){
            state.csk = payload.id
        }
    }
})
