import Vuex from 'vuex';

import data from '../api/data'
export default new Vuex.Store({
    state:{
        students:[],
        csk:[],
        rcb:[]
    },
    getters:{},
    actions:{
        getStudents(context){
            context.commit('setStudents')
        },
        addTeamMember(data){
            console.log(data)
        }
    },
    mutations:{
        setStudents(state){
            state.students = data.getStudents()
        }
    }
})
