import Vuex from 'vuex';
import Vue from 'vue';

Vue.use(Vuex);

import data from '../api/data'
export default new Vuex.Store({
    state:{
        students:[]
    },
    getters:{},
    actions:{
        getStudents(context){
            context.commit('setStudents')
        }
    },
    mutations:{
        setStudents(state){
            state.students = data.getStudents()
        }
    }
})
